import React, { useState } from 'react';
import styles from './styles.module.css'; 
function Note({ note, onDelete, onEdit }) {
  const [isOpen, setIsOpen] = useState(false);  
  const [isEditing, setIsEditing] = useState(false);  
  const [title, setTitle] = useState(note.title);
  const [description, setDescription] = useState(note.description);

  const toggleEdit = () => {
    // Wyłącz funkcję edytowania
  };

  const handleSave = () => {
    // Wyłącz funkcję zapisu
  };

  return (
    <div className={styles.noteContainer}>
      <div className={styles.noteHeader}>
        <h2 
          className={styles.noteTitle}
          onClick={() => setIsOpen(!isOpen)}  
        >
          {title}
        </h2>
        <div className={styles.noteButtons}>
          <button 
            onClick={toggleEdit}  
            className={styles.noteButtonEdit}
            disabled // Ustawienie przycisku jako nieaktywny
          >
            {isEditing ? 'Anuluj' : 'Edytuj'}
          </button>
          <button 
            onClick={() => {}} // Usunięcie akcji przypisanej do przycisku
            className={styles.noteButtonDelete}
            disabled // Ustawienie przycisku jako nieaktywny
          >
            Usuń
          </button>
        </div>
      </div>

      {(isOpen || isEditing) && ( 
        <div className="mt-4">
          {isEditing ? (
            <div>
              <input 
                type="text" 
                value={title} 
                onChange={(e) => setTitle(e.target.value)} 
                className={styles.noteInput}
              />
              <textarea 
                value={description} 
                onChange={(e) => setDescription(e.target.value)} 
                className={styles.noteInput}
              />
              <button 
                onClick={handleSave} 
                className={styles.noteButtonEdit}
                disabled // Ustawienie przycisku jako nieaktywny
              >
                Zapisz
              </button>
            </div>
          ) : (
            <p className={styles.noteDescription}>{description}</p> 
          )}
        </div>
      )}
    </div>
  );
}

export default Note;
