import React, { useState } from 'react';
import Modal from './Modal';
import styles from './styles.module.css';

function Note({ note, onDelete, onEdit }) {
  const [isOpen, setIsOpen] = useState(false); // Kontroluje wysuwanie szczegółów
  const [isEditing, setIsEditing] = useState(false);
  const [title, setTitle] = useState(note.title);
  const [description, setDescription] = useState(note.description);

  const toggleOpen = () => setIsOpen((prev) => !prev); // Przełącznik dla rozwijania

  const handleEditClick = () => {
    setIsEditing(true);
    setIsOpen(false); // Zamknięcie szczegółów, gdy zaczynamy edycję
  };

  const handleSave = () => {
    onEdit(note.id, title, description);
    setIsEditing(false);
  };

  const handleClose = () => setIsEditing(false);

  return (
    <div className={styles.noteContainer}>
      <div className={styles.noteHeader}>
        <h2 className={styles.noteTitle} onClick={toggleOpen}>
          {title}
        </h2>
        <div className={styles.noteButtons}>
          <button onClick={handleEditClick} className={styles.noteButtonEdit}>
            Edytuj
          </button>
          <button onClick={onDelete} className={styles.noteButtonDelete}>
            Usuń
          </button>
        </div>
      </div>

      {isOpen && ( // Renderujemy opis notatki tylko gdy isOpen jest true
        <div className={`${styles.noteDetails} ${isOpen ? styles.slideDown : ''}`}>
          <p className={styles.noteDescription}>{description}</p>
        </div>
      )}

      {isEditing && (
        <Modal
          title={title}
          description={description}
          onTitleChange={setTitle}
          onDescriptionChange={setDescription}
          onSave={handleSave}
          onClose={handleClose}
        />
      )}
    </div>
  );
}

export default Note;
