import React, { useState } from 'react';
import './Components/Note/styles.modules.css';  // Użycie pełnej ścieżki z uwzględnieniem katalogów

function Note({ note, onDelete, onEdit }) {
  const [isOpen, setIsOpen] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const [title, setTitle] = useState(note.title);
  const [description, setDescription] = useState(note.description);

  const handleSave = () => {
    onEdit(note.id, title, description);
    setIsEditing(false);
  };

  return (
    <div className={styles.noteContainer}>
      <div className="flex justify-between items-center">
        <h2 
          className={styles.noteTitle}
          onClick={() => setIsOpen(!isOpen)}
        >
          {title}
        </h2>
        <div className={styles.noteButtons}>
          <button 
            onClick={() => setIsEditing(true)} 
            className={styles.noteButtonEdit}
          >
            Edytuj
          </button>
          <button 
            onClick={onDelete} 
            className={styles.noteButtonDelete}
          >
            Usuń
          </button>
        </div>
      </div>
      {isOpen && (
        <div className="mt-4">
          {!isEditing ? (
            <p className={styles.noteDescription}>{description}</p>
          ) : (
            <div>
              <input 
                type="text" 
                value={title} 
                onChange={(e) => setTitle(e.target.value)} 
                className={styles.noteInput}
              />
              <textarea 
                value={description} 
                onChange={(e) => setDescription(e.target.value)} 
                className={styles.noteInput}
              />
              <button 
                onClick={handleSave} 
                className={styles.noteButtonEdit}
              >
                Zapisz
              </button>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default Note;
