import React, { useState } from 'react';
import styles from './styles.module.css';  // Poprawiona ścieżka i nazwa pliku

function Note({ note, onDelete, onEdit }) {
  const [isOpen, setIsOpen] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const [title, setTitle] = useState(note.title);
  const [description, setDescription] = useState(note.description);

  // Funkcja, która przełącza stan edycji
  const toggleEdit = () => {
    setIsEditing((prev) => !prev);
  };

  const handleSave = () => {
    onEdit(note.id, title, description);
    setIsEditing(false);  // Po zapisaniu wracamy do widoku zwykłego
  };

  return (
    <div className={styles.noteContainer}>
      <div className={styles.noteHeader}>
        <h2 
          className={styles.noteTitle}
          onClick={() => setIsOpen(!isOpen)}  // Przełącza widoczność opisu
        >
          {title}
        </h2>
        <div className={styles.noteButtons}>
          <button 
            onClick={toggleEdit}  // Przełączanie edycji
            className={styles.noteButtonEdit}
          >
            {isEditing ? 'Anuluj' : 'Edytuj'}
          </button>
          <button 
            onClick={onDelete} 
            className={styles.noteButtonDelete}
          >
            Usuń
          </button>
        </div>
      </div>

      {/* Sprawdzamy czy komponent jest rozwinięty */}
      {isOpen && (
        <div className="mt-4">
          {/* Jeśli jesteśmy w trybie edycji, pokazujemy formularz */}
          {isEditing ? (
            <div>
              <input 
                type="text" 
                value={title} 
                onChange={(e) => setTitle(e.target.value)} 
                className={styles.noteInput}
              />
              <textarea 
                value={description} 
                onChange={(e) => setDescription(e.target.value)} 
                className={styles.noteInput}
              />
              <button 
                onClick={handleSave} 
                className={styles.noteButtonEdit}
              >
                Zapisz
              </button>
            </div>
          ) : (
            <p className={styles.noteDescription}>{description}</p>  // Zwykły widok opisu
          )}
        </div>
      )}
    </div>
  );
}

export default Note;
