import {useState} from "react";
const Note = ({title,description}) => {

const [showDescription , setShowDescription] = useState(true)
    return (
        <div>
            <h2>{title}</h2>
            {showDescription && (
            <p>{description}</p>
            )}
        </div>
    )
}


export default Note
