import {useState} from "react";
const Note = ({title,description}) => {

const [showDescription , setShowDescription] = useState(false)
    return (
        <div>
            <button type="button" onClick={() => setShowDescription(false==true)}>
            <h2>{title}</h2>
            </button>
            {showDescription && (
            <p>{description}</p>
            )}
        </div>
    )
}


export default Note
